mapper.select:
SELECT * FROM youtube_channels


        AND is_verified = #{isVerified}


        AND channel_name ILIKE CONCAT('%', #{channelName}, '%')

mapper.update:
UPDATE youtube_channels
channel_name = #{channelName}, subscriber_count = #{subscriberCount}, video_count = #{videoCount}, thumbnail_url = #{thumbnailUrl}, is_verified = #{isVerified}, last_updated = NOW()
WHERE channel_id = #{channelId}

mapper.delete:
DELETE FROM youtube_channels
WHERE channel_id IN

    #{item}
